/**
 * Wrapper function for the lower_bound WASM module.
 * Automatically generated by generate_wrapper.py
 */

(function() {
  /**
   * Runs the LowerBound tool.
   * Uses a single stdin data string.   * @param {string} inputData - The input data.   * @param {Array<string>} args - CLI arguments (include flags and filenames for file inputs).
   * @returns {Promise<Object>} An object containing stdout and stderr outputs.
   */
  async function runLowerBound(inputData, args = []) {
    console.log("Starting runLowerBound");
    console.log("Arguments:", args);

    try {
      // Buffers for capturing stdout and stderr
      let stdoutBuffer = '';
      let stderrBuffer = '';

      // Module instantiation options
      const options = {
        locateFile: (path) => {
          if (path.endsWith('.wasm')) {
            const basePath = window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1' 
              ? '/wasm/' 
              : '/Biochef/wasm/';
            return basePath + path;
          }
          return path;
        },
        thisProgram: './lower_bound',
        noInitialRun: true,
        print: (text) => { stdoutBuffer += text + '\n'; },
        printErr: (text) => { stderrBuffer += text + '\n'; },
      };

      // Load the WASM module script
      await loadModuleScript('lower_bound');
      const moduleFactory = window['lower_bound'];
      if (typeof moduleFactory !== 'function') {
        throw new Error(`Module factory for lower_bound not available.`);
      }
      const module = await moduleFactory(options);

      // ------------------------------------------------------------------
      // Write inputs into the virtual filesystem
      // ------------------------------------------------------------------
      // Normalize and write single stdin input
      inputData = inputData.replace(/\r\n/g, '\n');
      module.FS.writeFile('input.txt', inputData);
      let fullArgs = args.slice();
      


      console.log("Executing module.callMain with arguments:", fullArgs);
      module.callMain(fullArgs);

      // ------------------------------------------------------------------
      // Collect outputs
      // ------------------------------------------------------------------
      // Single-output: capture stdout
      const outData = stdoutBuffer.trim();
      return { stdout: outData, stderr: stderrBuffer.trim() };

    } catch (err) {
      console.error(`Error in runLowerBound:`, err);
      throw err;
    }
  }

  /**
   * Dynamically loads the WASM module script if not already loaded.
   */
  function loadModuleScript(moduleName) {
    return new Promise((resolve, reject) => {
      if (window[moduleName]) return resolve();
      const script = document.createElement('script');
      const basePath = window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1' 
        ? '/wasm/' 
        : '/Biochef/wasm/';
      script.src = basePath + `${moduleName}.js`;
      script.onload = () => resolve();
      script.onerror = () => reject(new Error(`Failed to load ${moduleName}.js`));
      document.head.appendChild(script);
    });
  }

  // Expose globally
  window.run_lower_bound = runLowerBound;
})();